/* istanbul ignore file */
const morgan = require('morgan');
const config = require('./config');
const logger = require('./logger');
const { getClientIp } = require('../utils');

morgan.token('message', (req, res) => res.locals.errorMessage || '');
morgan.token('userId', (req) => req.userId || '');

morgan.token('ip', (req) => getClientIp(req));
const getIpFormat = () => (config.env === 'production' ? ':ip - ' : '');
const successResponseFormat = `${getIpFormat()}:userId :method :url :status - :response-time ms`;
const errorResponseFormat = `${getIpFormat()}:userId :method :url :status - :response-time ms - message: :message`;

const successHandler = morgan(successResponseFormat, {
  skip: (req, res) => res.statusCode >= 400,
  stream: { write: (message) => logger.info(message.trim()) },
});

const errorHandler = morgan(errorResponseFormat, {
  skip: (req, res) => res.statusCode < 400,
  stream: { write: (message) => logger.error(message.trim()) },
});

module.exports = {
  successHandler,
  errorHandler,
};
